import React, { useState } from "react";
import "./../styles/notewriter.css";
import PhysicalExamModal from "./PhysicalExamModal"; // Import Physical Exam Pop-Up

function NoteWriter() {
  const [noteText, setNoteText] = useState(""); // Editable portion
  const [physicalExamText, setPhysicalExamText] = useState(""); // Non-editable Physical Exam
  const [isExamModalOpen, setIsExamModalOpen] = useState(false);

  const handleChange = (e) => {
    setNoteText(e.target.value);
  };

  return (
    <div className="notewriter-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <h2>ðŸ©º EMR Note Writer</h2>
        <nav>
          <ul>
            <li className="active">New Note</li>
            <li>Patient History</li>
            <li>Orders</li>
            <li>Labs</li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="notewriter-content">
        <h1>Clinical Documentation</h1>

        <div className="notewriter-textarea-container">
          {/* Editable Section */}
          <textarea
            className="notewriter-textarea"
            placeholder="Type your clinical notes here..."
            value={noteText}
            onChange={handleChange}
          />

          {/* Non-Editable Physical Exam Section */}
          <div className="physical-exam-section">
            <h3>Physical Exam</h3>
            <pre>{physicalExamText || "No physical exam findings yet."}</pre>
          </div>
        </div>

        <div className="button-group">
          <button className="exam-button" onClick={() => setIsExamModalOpen(true)}>
            Open Physical Exam
          </button>
        </div>
      </main>

      {/* Physical Exam Pop-Up */}
      {isExamModalOpen && (
        <PhysicalExamModal
          setIsExamModalOpen={setIsExamModalOpen}
          setPhysicalExamText={setPhysicalExamText}
        />
      )}
    </div>
  );
}

export default NoteWriter;
