import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./components/firebaseConfig";
import Login from "./components/Login";
import Signup from "./components/Signup";
import NoteWriter from "./components/NoteWriter";
import PatientInfo from "./components/PatientInfo";  // Import the new component

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setIsAuthenticated(!!user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  if (loading) return <h2>Loading...</h2>;

  return (
    <Router>
      <div>
        {isAuthenticated && (
          <nav>
            <ul>
              <li><Link to="/note-writer">Note Writer</Link></li>
              <li><Link to="/patient-info">Patient Info</Link></li>
            </ul>
          </nav>
        )}
        <Routes>
          <Route path="/" element={isAuthenticated ? <Navigate to="/note-writer" /> : <Login />} />
          <Route path="/signup" element={isAuthenticated ? <Navigate to="/note-writer" /> : <Signup />} />
          <Route path="/note-writer" element={isAuthenticated ? <NoteWriter /> : <Navigate to="/" />} />
          <Route path="/patient-info" element={isAuthenticated ? <PatientInfo /> : <Navigate to="/" />} />
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
