    try {
      const patientDocRef = doc(db, "patients", patientName);
      const notesCollectionRef = collection(patientDocRef, "notes");
      const notesQuery = query(notesCollectionRef, orderBy("timestamp", "desc"), limit(5));
      const querySnapshot = await getDocs(notesQuery);
      const pastNotes = [];
      querySnapshot.forEach((doc) => {
        pastNotes.push(doc.data().noteText);
      });
      return pastNotes;
    } catch (error) {
      console.error("Error fetching past notes:", error);
      return [];
    }
  };

