  const generateSuggestion = async (currentText, patientName) => {
    const pastNotes = await fetchPastNotes(patientName);
    if (!pastNotes.length) return ""; // No past notes to analyze

    const prompt = `
      You are an AI medical assistant helping a doctor write clinical notes.
      The doctor prefers the following writing style:

      ${pastNotes.join("\n\n")}  

      Now, based on this style, suggest a likely next sentence for:
      "${currentText}"
    `;

    try {
      const response = await fetch("https://api.openai.com/v1/completions", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Authorization": `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`, // Replace with your actual API key securely
        },
        body: JSON.stringify({
          model: "gpt-4",
          prompt: prompt,
          max_tokens: 50,
          temperature: 0.7,
        }),
      });

      const data = await response.json();
      return data.choices[0].text.trim();
    } catch (error) {
      console.error("Error generating AI suggestion:", error);
      return "";
    }
  };





  const [predictions, setPredictions] = useState([]);

  const handleGeneratePrediction = async () => {
    if (!patientName) {
      alert('Please select a patient.');
      return;
    }
    const predictionText = await generateSuggestion(noteText, patientName);
    setPrediction(predictionText);
  };






  // Fetch list of patients from Firestore
  const fetchPatients = async () => {
    try {
      const notesCollectionRef = collection(db, "notes");
      const snapshot = await getDocs(notesCollectionRef);
      let patientList = snapshot.docs.map((doc) => doc.id);
      setPatients(patientList);
    } catch (error) {
      console.error("Error fetching patients:", error);
      alert("Failed to load patients.");
    }
  };
 // Save note under the selected patient's visit
