import React, { useState } from "react";
import "./../styles/notewriter.css";
import PhysicalExam from "./PhysicalExam"; // Import Physical Exam Component

function NoteWriter() {
  const [noteText, setNoteText] = useState("");
  const [aiSuggestion, setAiSuggestion] = useState("");
  const [physicalExamText, setPhysicalExamText] = useState("");

  const getAISuggestion = () => {
    if (noteText.trim() === "") return;

    // Simulated AI response (Replace with OpenAI API)
    const simulatedResponse =
      "Suggested Addition: Consider ordering a CBC and referring the patient to cardiology for further evaluation.";

    setAiSuggestion(simulatedResponse);
  };

  return (
    <div className="notewriter-container">
      {/* Sidebar Navigation */}
      <aside className="sidebar">
        <h2>ðŸ©º EMR Note Writer</h2>
        <nav>
          <ul>
            <li className="active">New Note</li>
            <li>Patient History</li>
            <li>Orders</li>
            <li>Labs</li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="notewriter-content">
        <h1>Clinical Documentation</h1>
        <textarea
          className="notewriter-textarea"
          placeholder="Type your clinical notes here..."
          value={noteText + "\n\n" + physicalExamText}
          onChange={(e) => setNoteText(e.target.value)}
        />

        <div className="button-group">
          <button className="ai-button" onClick={getAISuggestion}>
            Get AI Suggestion
          </button>
          <button className="save-button">Save Note</button>
        </div>

        {aiSuggestion && <p className="ai-suggestion">{aiSuggestion}</p>}
      </main>

      {/* Physical Exam Sidebar */}
      <PhysicalExam setPhysicalExamText={setPhysicalExamText} />
    </div>
  );
}

export default NoteWriter;
